import React, { useState, useEffect, memo } from 'react';
import './style/Projects.scss';
import { Card, Loading } from '../../components';
import setPageTitle from '../../utils/setPageTitle';
import fetchGithubApi from '../../utils/fetch';

function Projects() {
  const [repos, setRepos] = useState([]);
  const urlToFetch = 'https://api.github.com/users/caueperuque/repos';

  useEffect(() => {
    async function fetchData() {
      try {
        const repositories = await fetchGithubApi(urlToFetch);
        if (repositories) {
          const filteredRepositories = repositories
            .filter((repo) => !repo.name.startsWith('caue'));
          setRepos(filteredRepositories);
        }
      } catch (err) {
        console.error(err);
      }
    }

    fetchData();
  }, [urlToFetch]);

  useEffect(() => {
    async function fetchData() {
      try {
        const repositories = await fetchGithubApi(urlToFetch);
        if (repositories) {
          const filteredRepositories = repositories
            .filter((repo) => !repo.name.startsWith('caue'));
          setRepos(filteredRepositories);
        }
      } catch (err) {
        console.error(err);
      }
    }

    const interval = setInterval(fetchData, 5 * 60 * 1000); // 5 minutos em milissegundos

    // Executado quando o componente é desmontado
    return () => {
      clearInterval(interval); // Limpa o intervalo quando o componente é desmontado
    };
  }, [urlToFetch]);

  const renderCards = () => {
    if (repos.length < 1) {
      return <Loading />;
    }

    return repos.map((card) => (
      <Card
        key={ card.id }
        name={ card.name }
        description={ card.description ? card.description : 'teste' }
      />
    ));
  };

  return (
    <div className="background-color-grey projects">
      <div className="projects__title">
        <h2>Meus Projetos</h2>
      </div>
      <div className="projects__card-box">{renderCards()}</div>
    </div>
  );
}

export default memo(Projects);
